client_script:
frappe.web_form.on('qty', (field, value) => {
    	let data = frappe.web_form.get_values();
	let ticket_cost = 200
	if (data.workshop_pass) {
		ticket_cost = 250
	}
	frappe.web_form.set_value('amount', parseFloat(data.qty* ticket_cost))
});

frappe.web_form.on('workshop_pass', (field, value) => {
    	let data = frappe.web_form.get_values();
	let ticket_cost = 200
	if (data.workshop_pass) {
		ticket_cost = 250
	}
	frappe.web_form.set_value('amount', parseFloat(data.qty* ticket_cost))
});

$('.btn-form-submit').hide();
